INSERT INTO `tb_commentaire` (`id`, `pseudo`, `email`, `url`, `contenu`, `create_time`, `billet_id`) VALUES
(16, 'freax-n1', 'freax-n1@mail.com', 'http://freaxmind.no-ip.info', 'Hollywood ... ça fait rêver !', 1321458596, 6),
(17, 'gars_du_54', 'gars_du_54@mail.com', 'http://pauvre.skyblog.com', 'TROP LOL !!\r\n\r\nMDR', 1321458663, 6),
(18, 'Autre type', 'type@gmail.com', '', 'Qu''est ce que j''aurais aimé être producteur ...\r\n\r\nVive Tim Burton !!', 1321458732, 5),
(19, 'Scénariste mégalo', 'genial@mail.com', 'http://google.fr', 'Scénariste c''est un métier génial.\r\n\r\nJ''encourage les jeunes à imiter le grand Tim Burton.', 1321458789, 4),
(20, 'Scénariste dépressif', 'depression_mon_ami@mail.com', '', 'N''écoutez pas scénariste mégalo, vous allez vous tirer une balle ...', 1321458823, 4),
(21, 'Pti Rex', 'diplodocus@mail.com', 'http://dinosaure.fr', 'Les dinosaures ces géniales, pourquoi perdre son temps sur les films de Tim Burton quand on peut regarder jurrasik park ?\r\n\r\nFranchement ...', 1321458878, 3),
(22, 'Écolo bobo', 'bobo@greenpeace.com', '', 'C''est beau, avec plein de verdure ! Je comprend où Tim puisse son inspiration', 1321458926, 2),
(23, 'Mr Cynique', 'cynisme@raf.fr', '', 'Encore un coin pommé, pourquoi s''ennuyer à vivre la bas quand on peut vivre à Los Angeles ?\r\n\r\nIl ne pouvait pas naître ailleurs !\r\n\r\nSinon, je n''aime pas son travail.', 1321458994, 2);

